mapper.select:
select * from 
	(select rownum R, T.* from
		(select * from FREEBOARD order by idx asc) T -- 별칭;
) where R between #{startNo} and #{endNo}

select * from
(select rownum r ,T.* from
(select * from freeboard where like #{findText} order by idx desc) T
)
where r between #{startNo} and #{endNo}
